    private void jButton1_updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1_updateActionPerformed
        // TODO add your handling code here:
        if (!IDtxt.getText().equals("") && !typetxt.getText().equals("") && !Nametxt.getText().equals("") && !abbrevationtxt.getText().equals("") && !expirationtxt.getText().equals("") && !searchtxt.getText().equals("") && !pricetxt.getText().equals("") && !commenttxt.getText().equals("")) {

            Vaccine x = new Vaccine();
            x.set_vacId(Integer.parseInt(IDtxt.getText()));
            x.set_vacTradeName(Nametxt.getText());
            x.set_vacType(typetxt.getText());
            x.set_vacAbbreviation(abbrevationtxt.getText());
            x.set_comments(commenttxt.getText());
            x.set_vacExpirationYear(Integer.parseInt(expirationtxt.getText()));
            x.set_price(Integer.parseInt(pricetxt.getText()));

            if (x.updateVac()) {
                JOptionPane.showMessageDialog(null, "Updated Successfully ... !");
                resetPanelData();
            } else {

                JOptionPane.showMessageDialog(null, "Failed to Update ... !");

            }
        } else {

            JOptionPane.showMessageDialog(null, "Missing required Fields ... !  Please, complete them before submit .... !");

        }

    }//GEN-LAST:event_jButton1_updateActionPerformed

    private void jButton2_deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2_deleteActionPerformed
        // TODO add your handling code here:
        if (!searchtxt.getText().equals("")) {
            Vaccine x = new Vaccine();
            if (x.deleteVac(Integer.parseInt(searchtxt.getText()))) {
                JOptionPane.showMessageDialog(null, "Deleted Successfully ... !");
                resetPanelData();
            } else {
                JOptionPane.showMessageDialog(null, "Failed to Delete ... !");
            }
        } else {
            
            JOptionPane.showMessageDialog(null, "Missing required Fields ... !  Please, Search By ID before submit ... !");

        }
    }//GEN-LAST:event_jButton2_deleteActionPerformed

    private void jButton3_searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3_searchActionPerformed
      // TODO add your handling code here:
        if (!searchtxt.getText().equals("")) {
            Store x = new Store();
            Vaccine vac=new Vaccine();
            Store returned = x.searchVacById(Integer.parseInt(searchtxt.getText()));
            if (returned.vac.get_vacId()> 0) {
                setPanelData(returned);
            } else {
                JOptionPane.showMessageDialog(null, "Not Found ... !");
            }
        } else {

            JOptionPane.showMessageDialog(null, "Missing required Fields ... !  Please, Search By ID before submit ... !");

        }
    }//GEN-LAST:event_jButton3_searchActionPerformed


