   protected void resetPanelData() {
        
       IDtxt.setText(null);
       Nametxt.setText(null);
       abbrevationtxt.setText(null);
       commenttxt.setText(null);
       expirationtxt.setText(null);
       pricetxt.setText(null);
       typetxt.setText(null);
       searchtxt.setText(null);
    }
   
   protected void setPanelData(Store x) {
        Vaccine v = new Vaccine();
        
        IDtxt.setText("" + v.get_vacId());
        Nametxt.setText("" + v.get_vacTradeName());
        abbrevationtxt.setText("" + v.get_vacAbbreviation());
        expirationtxt.setText("" + v.get_vacExpirationYear());
        typetxt.setText("" + v.get_vacType());
        commenttxt.setText("" + v.get_vacComment());
        pricetxt.setText("" + v.get_price());

    }
   
   
   
